import * as Yup from 'yup';
import i18n from "i18next";
const phoneRegExp = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/

const SignupSchema = () =>
  Yup.object().shape({
    email: Yup.string()
      .email(i18n.t('invalid_email'))
      .required(i18n.t('required')),
    password: Yup.string()
      .min(8, i18n.t('min', { num: 8 }))
      .max(100, i18n.t('max', {  num: 100 }))
      .required(i18n.t('required')),
    confirmPassword: Yup.string()
      .min(8, i18n.t('min', { num: 8 }))
      .max(100, i18n.t('max', {  num: 100 }))
      .oneOf([Yup.ref('password'), null], i18n.t('password_not_match'))
      .required(i18n.t('required')),
    firstName: Yup.string()
      .min(1, i18n.t('min', { num: 1 }))
      .max(16, i18n.t('max', {  num: 16 }))
      .required(i18n.t('required')),
    lastName: Yup.string()
      .min(1, i18n.t('min', { num: 1 }))
      .max(16, i18n.t('max', {  num: 16 }))
      .required(i18n.t('required')),
    phone: Yup.string()
      .matches(/\(?([0-9]{3})\)?([ .-]?)([0-9]{3})\2([0-9]{4})/, {message: i18n.t('phone_validation'), excludeEmptyString: false})
      .required(i18n.t('required')),     
    acceptTerms: Yup.bool()
    // .oneOf([true], 'Accept Terms & Conditions is required')
    .required(i18n.t('required')),   
  });

export const LoginSchema = () =>
  Yup.object().shape({
    email: Yup.string()
      .email(i18n.t('invalid_email'))
      .required(i18n.t('required')),
    password: Yup.string().required(i18n.t('required')),
  });

export const ForgotPasswordSchema = () =>
  Yup.object().shape({
    email: Yup.string()
     .email(i18n.t('invalid_email'))
     .required(i18n.t('required')),
  });

export const ResetPasswordSchema = () =>
  Yup.object().shape({
    password: Yup.string()
      .min(5, i18n.t('min', { num: 5 }))
      .max(100, i18n.t('max', { num: 100 }))
      .required(i18n.t('required')),
    confirmPassword: Yup.string()
      .min(5, i18n.t('min', { num: 5 }))
      .max(100, i18n.t('max', { num: 100 }))
      .oneOf([Yup.ref('password'), null], i18n.t('password_not_match'))
      .required(i18n.t('required')),
  });

export const CurrentUserProfileSchema = () =>
  Yup.object().shape({
    email: Yup.string()
     .email(i18n.t('invalid_email'))
     .required(i18n.t('required')),
  });

export const CurrentUserPassRecoverySchema = () =>
  Yup.object().shape({
    password: Yup.string()
      .min(5, i18n.t('min', { num: 5 }))
      .max(100, i18n.t('max', { num: 100 }))
      .required(i18n.t('required')),
    confirmPassword: Yup.string()
      .min(5, i18n.t('min', { num: 5 }))
      .oneOf([Yup.ref('password'), null], i18n.t('password_not_match'))
      .required(i18n.t('required')),
  });

export const CreateDomainSchema = (values, domains) => {
  const errors = {};
  const isUniqueDomain = domains && domains.some(item => values.host.includes(item.host));
  if (!values.host) {
    errors.host = 'Required';
  } else if (!/^[a-zA-Z0-9][a-zA-Z0-9-]{1,61}[a-zA-Z0-9](?:\.[a-zA-Z]{2,})+$/.test(values.host)) {
    errors.host = 'Invalid host address';
  } else if (isUniqueDomain) {
    errors.host = 'Not unique host address';
  }
  return errors;
};

export const TriggerButtonTextSchema = () =>
  Yup.object().shape({
    title: Yup.string()
      .min(5, i18n.t('min', { num: 5 }))
      .max(20, i18n.t('max', { num: 20 }))
      .required(i18n.t('required')),
  });

export const CouponsSchema = () =>
  Yup.object().shape({
    value: Yup.string()
    .min(3, i18n.t('min', { num: 3 }))
    .max(20, i18n.t('max', { num: 20 }))
    .required(i18n.t('required')),
    resultText: Yup.string()
    .min(3, i18n.t('min', { num: 3 }))
    .max(20, i18n.t('max', { num: 20 }))
    .required(i18n.t('required')),
  });

export const StartScreenSchema = () =>
  Yup.object().shape({
    title: Yup.string()
    .min(3, i18n.t('min', { num: 3 }))
    .max(30, i18n.t('max', { num: 30 }))
    .required(i18n.t('required')),
    subtitle: Yup.string()
    .min(3, i18n.t('min', { num: 3 }))
    .max(70, i18n.t('max', { num: 70 }))
    .required(i18n.t('required')),
    button: Yup.string()
    .min(2, i18n.t('min', { num: 2 }))
    .max(6, i18n.t('max', { num: 6 }))
    .required(i18n.t('required')),
  });

export const ProgressScreenSchema = () =>
  Yup.object().shape({
    title: Yup.string()
    .min(3, i18n.t('min', { num: 3 }))
    .max(30, i18n.t('max', { num: 30}))
    .required(i18n.t('required')),
    subtitle: Yup.string()
    .min(3, i18n.t('min', { num: 3 }))
    .max(70, i18n.t('max', { num: 70 }))
    .required(i18n.t('required')),
  });

export const GeneralSettingsSchema = () =>
  Yup.object().shape({
    show_count: Yup.number()
      .min(1, i18n.t('min', { num: 1 }))
      .max(100, i18n.t('max', { num: 100 }))
      .positive("Number can't be negative")
      .required(i18n.t('required')),
    exp_copied: Yup.number()
      .min(1, i18n.t('min', { num: 1 }))
      .max(999, i18n.t('max', { num: 999 }))
      .positive("Number can't be negative")
      .required(i18n.t('required')),
  });

export const AddSiteUrlSchema = () =>
  Yup.object().shape({
    siteUrl: Yup.string()
     .min(3, i18n.t('min', { num: 3 }))
     .required(i18n.t('required')),
  });

export default SignupSchema;
