import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import { useSelector } from "react-redux";
// import Loader from '@components/Loader'




export const PublicRoute = ({ component: Components, ...rest }) => {
  const authentificated = useSelector(state => state.authentificationReducer.isAuthentificated);
return (
  <>
  <Route
    {...rest}
    render={props => (!authentificated ? <Components {...props} /> : <Redirect to="/" />)}
  />
</>
)
}



export const PrivateRoute = ({ component: Components, ...rest }) => {
  const authentificated = useSelector(state => state.authentificationReducer.isAuthentificated);
  const loaded = useSelector(state => state.authentificationReducer.loaded);
  const pending = useSelector(state => state.authentificationReducer.pending);
return(
  <>
  <Route
    {...rest}
    render={props => (pending ? (<div>Loading...</div>) : ((authentificated && loaded) ? (<Components {...props} />) : (<Redirect exact to="/login" />)))}
  />
  </>
)
}
