const LoginPage = ({isLoadingAuth, loginAction, pendingAction, addNotificationAction}) => {

    return (

        <div>
            <div>
                <h2>Log in</h2>
                <h3>
                    New User <Link to="/register">Registration</Link>
                </h3>
                <Formik
                    initialValues={{email: '', password: ''}}
                    validationSchema={LoginSchema}
                    // onSubmit={(values, actions) => {
