/* eslint-disable react/jsx-no-target-blank */
import React, {useState} from "react";
import {Link, useHistory} from "react-router-dom";
import {Button} from "@material-ui/core";
import Input from "@components/Form/Input";
import SignupSchema from "@helpers/Formik/validation";
import Formik from "@helpers/Formik";
import {Field} from "formik";
import PrivacyPopup from "@containers/PrivacyPopup/PrivacyPopup";


const RegisterPage = ({
                              isLoadingAuth,
                              registerAction,
                              pendingAction,
                              addNotificationAction,
                          }) => {
    const [isPrivacyOpened, setPrivacyOpened] = useState(false);
    let history = useHistory();
    const {t} = useTranslation();

    const handleSubmit2 = (data) => {
        return registerRequest(data)
            .then(() => history.push("/verify"))
            .then(() => {
                console.log('registered')
            });
    };

    const [checkboxValue, setCheckboxValue] = useState(false);

    return (
        <React.Fragment>
            {isPrivacyOpened && (
                <PrivacyPopup togglePrivacyPolicy={() => setPrivacyOpened((p) => !p)}/>
            )}
            <div className={css.auth__content}>
                <div className={css.auth__mainBox}>
                    <h2 className={css.auth__title}>{t("registration")}</h2>
                    <h3 className={`${css.auth__subtitle} ${css.sign_up}`}>
                        {t("registerd")} <Link to="/login">{t("login")}</Link>
                    </h3>
                    <Formik
                        initialValues={{
                            email: "",
                            password: "",
                            confirmPassword: "",
                            firstName: "",
                            lastName: "",
                            phone: "",

                        }}
                        validationSchema={SignupSchema}
                        onSubmit={(values, actions) => {
                            handleSubmit2(values).catch((error) => {
                                console.log(JSON.stringify(error));
                                actions.setErrors({
                                    email: " ",
                                    password: " ",
                                    confirmPassword: " ",
                                    firstName: " ",
                                    lastName: " ",
                                    phone: " ",
                                });
                                if (error?.notification) {
                                    addNotificationAction({type: "error", text: error.message});
                                    return
                                }
                                console.log(error)
                            });
                        }}
                    >
                        {({
                              setFieldTouched,
                              values,
                              errors,
                              touched,
                              handleChange,
                              handleSubmit,
                              isSubmitting,
                          }) => (
                            <form className={css.auth__form}>
                                <div className={css.flexBetween}>
                                    <Input

                                        className={cx([css.auth__form_registerInput])}
                                        label={t("firstName")}
                                        variant="outlined"
                                        type="firstName"
                                        name="firstName"
                                        error={errors.firstName && touched.firstName}
                                        errorText={touched.firstName && errors.firstName}
                                        onBlur={() => setFieldTouched("firstName", true, false)}
                                        value={values.firstName}
                                        onChange={handleChange}
                                    />

                                    <Input
                                        className={cx([
                                            css.auth__form_registerInput,
                                            css.auth__form_input_leftP,
                                            css.auth__form_paddRight,
                                        ])}
                                        label={t("lastName")}
                                        variant="outlined"
                                        type="lastName"
                                        name="lastName"
                                        error={errors.lastName && touched.lastName}
                                        errorText={touched.lastName && errors.lastName}
                                        onBlur={() => setFieldTouched("lastName", true, false)}
                                        value={values.lastName}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div className={css.flexBetween}>
                                    <Input
                                        // style={{ marginRight: "20px" }}
                                        className={cx([css.auth__form_registerInput])}
                                        label={t("email")}
                                        variant="outlined"
                                        type="email"
                                        name="email"
                                        error={errors.email && touched.email}
                                        errorText={touched.email && errors.email}
                                        onBlur={() => setFieldTouched("email", true, false)}
                                        value={values.email}
                                        onChange={handleChange}
                                    />
                                    <Input
                                        className={cx([
                                            css.auth__form_registerInput,
                                            css.auth__form_input_leftP,
                                            // css.auth__form_paddRight
                                        ])}
                                        label={t("phone")}
                                        variant="outlined"
                                        type="phone"
                                        name="phone"
                                        error={errors.phone && touched.phone}
                                        errorText={touched.phone && errors.phone}
                                        onBlur={() => setFieldTouched("phone", true, false)}
                                        value={values.phone}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div className={css.flexBetween}>
                                    <Input
                                        // style={{ marginRight: "20px" }}
                                        className={css.auth__form_registerInput}
                                        label={t("password")}
                                        variant="outlined"
                                        type="password"
                                        name="password"
                                        error={errors.password && touched.password}
                                        errorText={touched.password && errors.password}
                                        onBlur={() => setFieldTouched("password", true, false)}
                                        value={values.password}
                                        onChange={handleChange}
                                    />
                                    <Input
                                        className={cx([
                                            css.auth__form_registerInput,
                                            css.auth__form_input_leftP,
                                            css.auth__form_paddRight,
                                        ])}
                                        label={t("password_confirm")}
                                        variant="outlined"
                                        type="password"
                                        name="confirmPassword"
                                        error={errors.confirmPassword && touched.confirmPassword}
                                        errorText={
                                            touched.confirmPassword && errors.confirmPassword
                                        }
                                        onBlur={() =>
                                            setFieldTouched("confirmPassword", true, false)
                                        }
                                        value={values.confirmPassword}
                                        onChange={handleChange}
                                    />
                                </div>

                                <div
                                    className="form-group form-check"
                                    style={{
                                        display: "flex",
                                        justifyContent: "center",
                                        alignItems: "center",
                                    }}
                                >
                                    <Field
                                        type="checkbox"
                                        name="acceptTerms"
                                        style={{marginRight: "10px"}}
                                        // error={errors.acceptTerms && touched.acceptTerms}
                                        // errorText={touched.acceptTerms && errors.acceptTerms}
                                        error={errors.acceptTerms && touched.acceptTerms}
                                        errorText={
                                            touched.acceptTerms && errors.acceptTerms

                                        }
                                        // checked={checkboxValue}
                                        // onChange={() => console.log(setCheckboxValue(!checkboxValue))}
                                    />
                                    <p className={css.auth__policy}>
                                        {t("privacy_first")} <br/>
                                        <p
                                            className={css.auth__privicyLink}
                                            onClick={() => setPrivacyOpened(true)}
                                        >
                                            {t("privacy_second")}
                                        </p>
                                    </p>


                                </div>
                                {/* {!checkboxValue ? (<span style={{ display: 'flex', color: 'red', justifyContent: 'center', fontSize: '14px'}}>Accept terms of use</span>) : null} */}
                                <Button
                                    type="submit"
                                    color="primary"
                                    variant="contained"
                                    className={css.auth__button}
                                    // disabled={isSubmitting || isLoadingAuth}
                                    onClick={handleSubmit}
                                >
                                    {t("register")}
                                </Button>
                            </form>
                        )}
                    </Formik>
                </div>
            </div>
        </React.Fragment>
    );
};


export default RegisterPage
